Code - 80ms // Code by ajcxsu // Problem: LCS2 #include&lt;bits/stdc++.h&gt; using namespace std; const int N=2e6+5; int len[N], fa[N], ch[N][26]; int lst=1, idx=1; inline void add(int c) { int p=lst, np=lst=++idx; len[np]=len[p]+1; for(; p &amp;&amp; !ch[p][c]; p=fa[p]) ch[p][c]=np; if(!p) fa[np]=1; else { int q=ch[p][c]; if(len[q]==len[p]+1) fa[np]=q; else { int nq=++idx; for(int i=0; i&lt;26; i++) ch[nq][i]=ch[q][i]; fa[nq]=fa[q]; len[nq]=len[p]+1, fa[q]=fa[np]=nq; for(; p &amp;&amp; ch[p][c]==q; p=fa[p]) ch[p][c]=nq; } } } int qu[N], h, t; char str[N]; int bu[N], id[N]; int su[N], f[N]; void solve() { int x=1, l=0; memset(su, 0, sizeof(su)); for(char *p=str; *p; p++) { if(ch[x][*p-'a']) l++, x=ch[x][*p-'a']; else { while(x &amp;&amp; !ch[x][*p-'a']) x=fa[x]; if(!x) l=0, x=1; else l=len[x]+1, x=ch[x][*p-'a']; } su[x]=max(su[x], l); } for(int i=idx; i&gt;=1; i--) su[fa[id[i]]]=max(su[fa[id[i]]], su[id[i]]); for(int i=1; i&lt;=idx; i++) len[i]=min(len[i], su[i]); } int main() { scanf(&quot;%s&quot;, str); for(char *p=str; *p; p++) add(*p-'a'); for(int i=1; i&lt;=idx; i++) bu[len[i]]++; for(int i=1; i&lt;=idx; i++) bu[i]+=bu[i-1]; for(int i=idx; i&gt;=1; i--) id[bu[len[i]]--]=i; while(scanf(&quot;%s&quot;, str)==1) solve(); int ans=0; for(int i=1; i&lt;=idx; i++) ans=max(ans, len[i]); printf(&quot;%d\n&quot;, ans); return 0; }  Code - TLE // Code by ajcxsu // Problem: LCS2 #include&lt;bits/stdc++.h&gt; using namespace std; const int N=2e6+5; int len[N], fa[N], ch[N][26], in[N]; int f[N], lst=1, idx=1; inline void add(int c, int val) { int p=lst, np=lst=++idx; len[np]=len[p]+1; f[np]=1&lt;&lt;val; for(; p &amp;&amp; !ch[p][c]; p=fa[p]) ch[p][c]=np; if(!p) fa[np]=1; else { int q=ch[p][c]; if(len[q]==len[p]+1) fa[np]=q; else { int nq=++idx; for(int i=0; i&lt;26; i++) ch[nq][i]=ch[q][i]; fa[nq]=fa[q], f[nq]=f[q]; len[nq]=len[p]+1, fa[q]=fa[np]=nq; for(; p &amp;&amp; ch[p][c]==q; p=fa[p]) ch[p][c]=nq; } } } int qu[N], h, t; char str[N]; int main() { int nidx=0; while(scanf(&quot;%s&quot;, str)==1) { lst=1; for(char *p=str; *p; p++) add(*p-'a', nidx); ++nidx; } nidx=(1&lt;&lt;nidx)-1; int ans=0, na, nf; for(int i=1; i&lt;=idx; i++) in[fa[i]]++; for(int i=1; i&lt;=idx; i++) if(!in[i]) qu[t++]=i; while(h!=t) { na=qu[h++], nf=fa[na]; if(f[na]==nidx &amp;&amp; len[na]&gt;ans) ans=len[na]; f[nf]|=f[na], in[nf]--; if(!in[nf]) qu[t++]=nf; } printf(&quot;%d\n&quot;, ans); return 0; }

